package forloop;

public class forloopdemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//bringing three concepts together
		// int is your data, a is your value and it can be variable, a++ is your increment. a<=10 is your condition
// private is local, public means outside can see
		for(int a=1; a<=10; a++) {
			System.out.println(a);
		}
		
		
		
		
		
	}

}
